import React, { useState } from "react";
import './TryItOut.css';

const TryItOut = () => {
  const [selectedOption, setSelectedOption] = useState("");
  const [articleText, setArticleText] = useState("");
  const [youtubeLink, setYoutubeLink] = useState("");
  const [contextText, setContextText] = useState("");

  // Handle submit button click
  const handleSubmit = () => {
    // Add logic for handling the submit action here, like sending data to backend or processing
    alert("Submitted!");
  };

  return (
    <div className="try-it-out-container">
      <div className="toggle-buttons">
        <button
          className="toggle-button"
          onClick={() => setSelectedOption("article")}
        >
          Enter Article Text
        </button>
        <button
          className="toggle-button"
          onClick={() => setSelectedOption("youtube")}
        >
          Enter YouTube Video Link
        </button>
      </div>

      {selectedOption === "article" && (
        <textarea
          className="text-box"
          placeholder="Copy-paste the contents of an article you would like to be analyzed! Be sure to add any context at the beginning."
          value={articleText}
          onChange={(e) => setArticleText(e.target.value)}
        ></textarea>
      )}
      {selectedOption === "youtube" && (
        <>
          <input
            className="text-box"
            type="text"
            placeholder="Enter the YouTube video link here"
            value={youtubeLink}
            onChange={(e) => setYoutubeLink(e.target.value)}
          />
          <textarea
            className="text-box"
            placeholder="Add context for the video (optional)"
            value={contextText}
            onChange={(e) => setContextText(e.target.value)}
          ></textarea>
        </>
      )}

      {/* Conditionally render the Submit button after selecting an option */}
      {selectedOption && (
        <button className="submit-button" onClick={handleSubmit}>
          Submit
        </button>
      )}
    </div>
  );
};

export default TryItOut;
