import React, { useState } from 'react';

function TryItOut() {
  const [isArticle, setIsArticle] = useState(true);

  return (
    <div>
      <h2>Try It Out</h2>
      <div>
        <button onClick={() => setIsArticle(true)}>Enter Article Text</button>
        <button onClick={() => setIsArticle(false)}>Enter YouTube Video Link</button>
      </div>
      
      {isArticle ? (
        <div>
          <textarea placeholder="Copy-paste the contents of an article here, including any context."></textarea>
        </div>
      ) : (
        <div>
          <input type="text" placeholder="Enter YouTube video link here" />
          <textarea placeholder="Add any context here"></textarea>
        </div>
      )}
      
      <button>Submit</button>
    </div>
  );
}

export default TryItOut;
