import React, { useState } from "react";
import './TryItOut.css';

const TryItOut = () => {
  const [selectedOption, setSelectedOption] = useState("");

  return (
    <div className="try-it-out-container">
      <div className="toggle-buttons">
        <button
          className="toggle-button"
          onClick={() => setSelectedOption("article")}
        >
          Enter Article Text
        </button>
        <button
          className="toggle-button"
          onClick={() => setSelectedOption("youtube")}
        >
          Enter YouTube Video Link
        </button>
      </div>

      {selectedOption === "article" && (
        <textarea
          className="text-box"
          placeholder="Copy-paste the contents of an article you would like to be analysed! Be sure to add any context at the beginning"
        ></textarea>
      )}
      {selectedOption === "youtube" && (
        <>
          <input
            className="text-box"
            type="text"
            placeholder="Enter the YouTube video link here"
          />
          <textarea
            className="text-box"
            placeholder="Add context for the video (optional)"
          ></textarea>
        </>
      )}
    </div>
  );
};

export default TryItOut;
