import React, { useState } from 'react';
import axios from 'axios';

const TwitterBotPage = () => {
  const [username, setUsername] = useState("");
  const [tweetUrl, setTweetUrl] = useState("");
  const [isProcessing, setIsProcessing] = useState(false);
  const [responseMessage, setResponseMessage] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsProcessing(true);
    setResponseMessage("");

    try {
      // Send the data to your backend for processing
      const response = await axios.post("http://localhost:5000/twitter-bot", {
        username,
        tweetUrl,
      }, {
        headers: {
          "Content-Type": "application/json",
        },
      });

      setResponseMessage(response.data.message);
    } catch (error) {
      console.error('Error in Twitter bot submission:', error);
      setResponseMessage("An error occurred. Please try again.");
    } finally {
      setIsProcessing(false);
    }
  };

  return (
    <div className="page-content">
      <div className="twitter-bot-container">
        <h1>Twitter Bot</h1>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label >Enter the Twitter account user tag:</label>
            <input className="text-box"
              type="text"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              placeholder="@username"
              required
            />
          </div>
          <div className="form-group">
            <label>Enter the post link here by the same user:</label>
            <input className="text-box"
              type="url"
              value={tweetUrl}
              onChange={(e) => setTweetUrl(e.target.value)}
              placeholder="https://twitter.com/user/status/tweet_id"
              required
            />
          </div>
          <button className='submit-button' type="submit" disabled={isProcessing}>
            {isProcessing ? 'Processing...' : 'Send Tweet'}
          </button>
        </form>

        {responseMessage && <p>{responseMessage}</p>}
      </div>
    </div>
  );
};

export default TwitterBotPage;
