import React, { useState } from "react";
import './TryItOut.css';
import { useNavigate } from 'react-router-dom'; // For navigation

const TryItOut = () => {
  const [selectedOption, setSelectedOption] = useState("");
  const [youtubeLink, setYoutubeLink] = useState("");
  const [contextText, setContextText] = useState("");
  const navigate = useNavigate();

  // Handle submit button click
  const handleSubmit = async () => {
    // Validate URL
    if (!youtubeLink) {
      alert("Please enter a valid YouTube link!");
      return;
    }

    const data = { youtubeLink, contextText };

    // Call the backend API
    try {
      const response = await fetch('http://localhost:5000/process_youtube', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(data),
      });

      const result = await response.json();
      if (response.ok) {
        // Navigate to the results page with the summary text
        navigate('/results', { state: { summaryText: result.summary } });
      } else {
        alert("Error: " + result.error);
      }
    } catch (error) {
      console.error("Error during submission:", error);
      alert("Failed to process. Please try again.");
    }
  };

  return (
    <div className="page-content">
      <div className="try-it-out-container">
        <div className="toggle-buttons">
          <button className="toggle-button" onClick={() => setSelectedOption("youtube")}>
            Enter YouTube Video Link
          </button>
        </div>

        {selectedOption === "youtube" && (
          <>
            <input
              className="text-box"
              type="text"
              placeholder="Enter the YouTube video link here."
              value={youtubeLink}
              onChange={(e) => setYoutubeLink(e.target.value)}
            />
            <textarea
              className="text-box"
              placeholder="Add context for the video (optional)"
              value={contextText}
              onChange={(e) => setContextText(e.target.value)}
            ></textarea>
          </>
        )}

        {selectedOption && (
          <button className="submit-button" onClick={handleSubmit}>
            Submit
          </button>
        )}
      </div>
    </div>
  );
};

export default TryItOut;
