import React, { useState } from 'react';
import { useLocation } from 'react-router-dom';
import axios from 'axios';

const TwitterBotPage = () => {
  const { state } = useLocation();
  const [isProcessing, setIsProcessing] = useState(false);
  const [responseMessage, setResponseMessage] = useState("");

  const handleConfirmPost = async () => {
    setIsProcessing(true);
    setResponseMessage("");

    try {
      const response = await axios.post("http://localhost:5000/twitter-bot", {
        summary: state.summary, // Pass the summary text directly
      }, {
        headers: {
          "Content-Type": "application/json",
        },
      });

      setResponseMessage(response.data.message);
    } catch (error) {
      console.error('Error in Twitter bot submission:', error);
      setResponseMessage("An error occurred. Please try again.");
    } finally {
      setIsProcessing(false);
    }
  };

  return (
    <div className="page-content">
      <div className="twitter-bot-container">
        <h1>Twitter Bot</h1>
        <p>Summary to be posted: {state.summary}</p>
        <button 
          className='submit-button' 
          onClick={handleConfirmPost} 
          disabled={isProcessing}
        >
          {isProcessing ? 'Posting...' : 'Confirm Post'}
        </button>

        {responseMessage && <p>{responseMessage}</p>}
      </div>
    </div>
  );
};

export default TwitterBotPage;
