import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

function TryItOut() {
  const [selectedOption, setSelectedOption] = useState('');
  const [url, setUrl] = useState('');
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      // Send YouTube URL to backend and start processing
      const response = await axios.post('http://127.0.0.1:5000/process', { url });

      // Once the response is received, navigate to the loading page
      navigate('/loading');

      // Simulating loading page transition (this will be replaced with backend logic)
      setTimeout(() => {
        navigate('/results');
      }, 3000); // Simulate 3 seconds delay for loading
    } catch (error) {
      console.error('Error fetching summary:', error);
    }
  };

  return (
    <div className="try-it-out-container">
      <h2>Try It Out</h2>

      {/* Toggle Buttons for selecting input type */}
      <div className="toggle-buttons">
        <button className="toggle-button" onClick={() => setSelectedOption('text')}>
          Enter Article Text
        </button>
        <button className="toggle-button" onClick={() => setSelectedOption('youtube')}>
          Enter YouTube URL
        </button>
      </div>

      {/* Conditional rendering for YouTube URL form */}
      {selectedOption === 'youtube' && (
        <form onSubmit={handleSubmit}>
          <textarea
            className="text-box"
            placeholder="Enter YouTube URL"
            value={url}
            onChange={(e) => setUrl(e.target.value)}
            required
          />
          <button className="submit-button" type="submit" disabled={loading}>
            {loading ? 'Processing...' : 'Submit'}
          </button>
        </form>
      )}
    </div>
  );
}

export default TryItOut;
