import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import LoadingPage from './LoadingPage';  // Your loading page component
import ResultsPage from './ResultsPage';  // Results page component

const TryItOutPage = () => {
  const [isLoading, setIsLoading] = useState(false);
  const [summary, setSummary] = useState(null);

  const handleSubmit = async (url) => {
      setIsLoading(true);  // Show loading screen

      try {
          // Send the YouTube URL to the Flask server for processing
          const response = await fetch('http://127.0.0.1:5000/process', {
              method: 'POST',
              headers: {
                  'Content-Type': 'application/json',
              },
              body: JSON.stringify({ url }),
          });

          const data = await response.json();

          // Fetch the results after processing
          if (data.summary) {
              setSummary(data.summary);
          }
      } catch (error) {
          console.error('Error processing request:', error);
      } finally {
          setIsLoading(false);  // Hide loading screen after process is done
      }
  };

  return (
    <div className="page-content">
      <div className="try-it-out-container">
        <h2>Try It Out</h2>

        {/* Toggle Buttons for selecting input type */}
        <div className="toggle-buttons">
          <button className="toggle-button" onClick={() => setSelectedOption('text')}>
            Enter Article Text
          </button>
          <button className="toggle-button" onClick={() => setSelectedOption('youtube')}>
            Enter YouTube URL
          </button>
        </div>

        {/* YouTube URL form */}
        {selectedOption === 'youtube' && (
          <form onSubmit={handleSubmit}>
            <textarea
              className="text-box"
              placeholder="Enter YouTube URL"
              value={url}
              onChange={(e) => setUrl(e.target.value)}
              required
            />
            
            {/* Optional Context Box */}
            <textarea
              className="text-box"
              placeholder="Optional context for analysis (if any)"
              value={context}
              onChange={(e) => setContext(e.target.value)}
            />
            
            <button className="submit-button" type="submit" disabled={loading}>
              {loading ? 'Processing...' : 'Submit'}
            </button>
          </form>
        )}

        {/* Article Text form (hidden for now, can be used later) */}
        {selectedOption === 'text' && (
          <form>
            <textarea
              className="text-box"
              placeholder="Enter Article Text"
              required
            />
            
            {/* Optional context for article text */}
            <textarea
              className="text-box"
              placeholder="Optional context for analysis (if any)"
              value={context}
              onChange={(e) => setContext(e.target.value)}
            />
            
            <button className="submit-button" type="submit" disabled={loading}>
              {loading ? 'Processing...' : 'Submit'}
            </button>
          </form>
        )}
      </div>
      </div>
  );
}

export default TryItOut;
