import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import LoadingPage from './LoadingPage';  // Your loading page component
import ResultsPage from './ResultsPage';  // Results page component

function TryItOut() {
  const [selectedOption, setSelectedOption] = useState('');
  const [url, setUrl] = useState('');
  const [context, setContext] = useState('');
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      // Send YouTube URL to backend and start processing
      const response = await axios.post('http://127.0.0.1:5000/process', { url });

      // Once the response is received, navigate to the loading page
      navigate('/loading');

      // Simulating loading page transition (this will be replaced with backend logic)
      setTimeout(() => {
        navigate('/results');
      }, 3000); // Simulate 3 seconds delay for loading
    } catch (error) {
      console.error('Error fetching summary:', error);
    }
  };

  return (
    <div className="page-content">
      <div className="try-it-out-container">
        <h2>Try It Out</h2>

        {/* Toggle Buttons for selecting input type */}
        <div className="toggle-buttons">
          <button className="toggle-button" onClick={() => setSelectedOption('text')}>
            Enter Article Text
          </button>
          <button className="toggle-button" onClick={() => setSelectedOption('youtube')}>
            Enter YouTube URL
          </button>
        </div>

        {/* YouTube URL form */}
        {selectedOption === 'youtube' && (
          <form onSubmit={handleSubmit}>
            <textarea
              className="text-box"
              placeholder="Enter YouTube URL"
              value={url}
              onChange={(e) => setUrl(e.target.value)}
              required
            />
            
            {/* Optional Context Box */}
            <textarea
              className="text-box"
              placeholder="Optional context for analysis (if any)"
              value={context}
              onChange={(e) => setContext(e.target.value)}
            />
            
            <button className="submit-button" type="submit" disabled={loading}>
              {loading ? 'Processing...' : 'Submit'}
            </button>
          </form>
        )}

        {/* Article Text form (hidden for now, can be used later) */}
        {selectedOption === 'text' && (
          <form>
            <textarea
              className="text-box"
              placeholder="Enter Article Text"
              required
            />
            
            {/* Optional context for article text */}
            <textarea
              className="text-box"
              placeholder="Optional context for analysis (if any)"
              value={context}
              onChange={(e) => setContext(e.target.value)}
            />
            
            <button className="submit-button" type="submit" disabled={loading}>
              {loading ? 'Processing...' : 'Submit'}
            </button>
          </form>
        )}
      </div>
      </div>
  );
}

export default TryItOut;
