import React, { useState, useEffect } from "react";
import './LoadingScreen.css'; // Import the corresponding CSS file

const LoadingPage = () => {
  const [progress, setProgress] = useState(0);
  const [feministFact, setFeministFact] = useState("");

  // Update progress bar and random feminist fact every 1 second
  useEffect(() => {
    // Array of feminist facts that will be displayed randomly
    const feministFacts = [
      "Did you know? The first woman to vote in the United States was in Wyoming in 1869!",
      "The term 'feminism' was coined in the late 19th century to define the belief in equality of the sexes.",
      "In 2018, Iceland became the first country to make equal pay for women a legal requirement.",
      "The first women’s suffrage movement started in 1848 with the Seneca Falls Convention in New York.",
      "The first woman in space was Valentina Tereshkova from the Soviet Union, who launched in 1963.",
      "Ruth Bader Ginsburg was the second woman appointed to the U.S. Supreme Court and was known for her advocacy for women’s rights.",
      "In 1972, Title IX of the Education Amendments was passed, prohibiting gender discrimination in federally funded education programs.",
      "Women in Saudi Arabia gained the right to drive in 2018 after a decades-long campaign for freedom and equality."
    ];

    const progressInterval = setInterval(() => {
      if (progress < 100) {
        setProgress((prevProgress) => prevProgress + 1);
      }
    }, 50); // Adjust speed of progress here

    const factInterval = setInterval(() => {
      const randomFact = feministFacts[Math.floor(Math.random() * feministFacts.length)];
      setFeministFact(randomFact);
    }, 4000); // Change fact every 4 seconds

    return () => {
      clearInterval(progressInterval);
      clearInterval(factInterval);
    };
  }, [progress]); // Only depend on progress since feministFacts is now inside useEffect

  return (
    <div className="loading-container">
      <h2>Processing... Please be patient!</h2>
      <div className="progress-bar-container">
        <div
          className="progress-bar"
          style={{ width: `${progress}%` }}
        ></div>
      </div>
      <p className="loading-text">Taking the video...</p>
      <p className="loading-text">Analyzing the audio (this will take time based on length of video, please have patience!)</p>
      <p className="loading-text">Summarizing the text...</p>
      <div className="feminist-fact">
        <p>{feministFact}</p>
      </div>
    </div>
  );
};

export default LoadingPage;
